import React from 'react';
import './App.css';
import Navbar from './containers/navbar'
import Sidebar from './containers/sidebar'
import Calendar from './components/calendar'

export default class App extends React.Component {

  state = {
    today: Date(Date.now()),
    menuBtn: false,
    user: {
      name: 'Pidgey',
      calendars: ['Personal Calendar', 'General Workspace Calendar', 'Project 1 Calendar', 'Project 2 Calendar'], //Objects? IDs?
      events: ['Terrorize New Pokemon Trainers', 'Event 1', 'Event 2', 'Event 3', 'Event 4'], //Objects? IDs?
    },
    sidebar: false,
  }

  componentDidMount(){
    this.interval = setInterval(()=>{this.setState({today: Date(Date.now())})}, 1000)
  }

  hamburgerBtn = () => {
    this.setState({
      menuBtn: !this.state.menuBtn,
      sidebar: !this.state.sidebar
    })
  }

  openSidebar = () => {
    if (this.state.sidebar === false){
      return null
    }
    else {
      return < Sidebar user={{...this.state.user}} /> 
    }
  }

  render(){
    return (
      <div className="App">
      < Navbar hamburgerBtn={this.hamburgerBtn} menuBtnState={this.state.menuBtn} username={this.state.user.name} />
      {this.openSidebar()}
      < Calendar today={this.state.today} /> 
      </div>
    );
  }
}
