        year: this.props.today.split(' ')[3],
        month: this.props.today.split(' ')[1],
        day: this.props.today.split(' ')[2],
    }

    //Testing purposes only
    createFeb = () => {
        let february = []
        for (let n = 0; n < 31; n++){
            february.push(n+1)
        }
