import React, { Fragment } from 'react'

const Sidebar = (props) => {

    const [viewState, setviewState] = useState(false);

    const toggleCalendarForm = () => {
        setviewState(!viewState)
    };

    useEffect()

    const setView = () => {
        if (viewState === false){
            return <Fragment>
                <h1>Upcoming Events</h1>
                <ol className='upcoming-events'>
                    {createEvents()}
                </ol>
                <h1>Your Calendars</h1>
                <ol className='user-calendars'>
                    {createCalendars()}
                </ol>
                <button className='open-calendar-form' onClick={toggleCalendarForm}></button>
            </Fragment>
        }
        else{
            return <Fragment>
                <h2>Create New Calendar</h2>
                <form className='create-calendar' onSubmit={submitCalendar}>
                    <label>Name</label><br/>
                    <input type='text' name='calendarName' />
                    <input type='submit' value='submit' />
                </form>
            </Fragment>
        }
    }

    const submitCalendar = () => {
        // fetch('http://localhost:3000/calendars', {
        //     method: "POST",
        //     headers: {

        //     },
        //     body: JSON.stringify({

        //     })
        // })
        
    }

    const createEvents = () => {
        return props.user.events.map(event=>{
            return <li key={event.id}>{event.name}</li>
        })
    };

    const createCalendars = () => {
        return props.user.calendars.map(calendar=>{
            return <li key={calendar.id}>{calendar.name}</li>
        })
    };

    return <div className='sidebar'>
        {setView()}
    </div>
}

export default Sidebar;
