import React from 'react'

export default class DayDock extends React.Component{

    state = {
        form: false,
        date: new Date(this.props.spotlight),
    }

    handleClick = () => {
        this.setState({
            form: !this.state.form
        })
    }
    
    showDock = () => {
        if (this.state.form){
            return <Fragment>
                <h2>DayDock for {this.state.date.toDateString()}</h2>
                <h2> Events </h2>
                <ul className='events-list'>
                    <li>Event 1</li>
                    <li>Event 2</li>
                    <li>Event 3</li>
                    <li>Event 4</li>
                </ul>
                <button className='create-event' onClick={this.handleClick}>Create Event</button>)
            </Fragment>
        }
        else if (!this.state.form){
            return <Fragment>
                <h2>Create New Event</h2> 
                <form className='events-form'>
                    <label>Name:
                        <input type='text' placeholder='Enter event name here...' />
                    </label>
                    <label>Description:
                        <input type='text-area' placeholder='(Optional)' />
                    </label>
                    <input type='submit' value='submit' />
                </form>
            </Fragment>
        }
    }

    render(){
        return <div className='daydock'>
            {this.showDock()}
        </div>
    }
}
