import React from 'react';
import Year from './year';
import Month from './month';
import Day from './day';


export default class Calendar extends React.Component{

    state = {
        yearSelection: this.props.today.split(' ')[3],
        monthSelection: this.props.today.split(' ')[1],
        daySelection: this.props.today.split(' ')[2],
    }

    //Testing purposes only
    createFeb = () => {
        let february = []
        for (let n = 0; n < 31; n++){
            february.push(n+1)
        }
        return february;
    }

    createDays = () => {
        return this.createFeb().map((day)=>{
            return < Day num={day} events={{}} />
        })
    }

    render(){
        return <div className="calendar">
            <h1>Today is...{this.props.today}</h1>
            < Year today={this.props.today}/>
            < Month today={this.props.today}/>
            <div className='days-container'>
                {this.createDays()}
            </div>
        </div>
    }

}


